// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

interface OracleInterface {
    function requestData(uint256 requestId) external;
}

abstract contract OracleClient {

    address _oracleAddr;
    uint256 _requestCounter = 0;

    constructor(address oracleAd) {
        _oracleAddr = oracleAd;
    }

    modifier oracleOnly() { require(msg.sender == _oracleAddr); _; }

    function requestDataFromOracle() internal returns (uint256) {
        OracleInterface(_oracleAddr).requestData(++_requestCounter);
        return _requestCounter;
    }

    function receiveDataFromOracle(uint256 requestId, bytes memory data) public virtual;
}

abstract contract Oracle is OracleInterface {

    event request(uint256 requestId, address caller);

    address public trustedServer;

    modifier trusted() { require(msg.sender == trustedServer); _; }

    constructor(address serverAddr) {
        trustedServer = serverAddr;
    }

    function requestData(uint256 requestId) public {
        emit request(requestId, msg.sender);
    }

    function replyData(uint256 requestId, address caller, bytes memory data) public virtual trusted {
        OracleClient(caller).receiveDataFromOracle(requestId, data);
    }
}


contract TemperatureOracle is Oracle {
    constructor(address serverAddr) Oracle(serverAddr) {}
}


abstract contract TemperatureOracleClient is OracleClient {

    constructor(address oracleAd) OracleClient(oracleAd) {}

    function requestTemperatureFromOracle() internal returns (uint256) {
        requestDataFromOracle();
        return _requestCounter;
    }

    function receiveDataFromOracle(uint256 requestId, int256 temp1) public oracleOnly {
        // (int256 temp1) = abi.decode(data, (int256));
        receiveTemperatureFromOracle(requestId, temp1);
    }

    function receiveTemperatureFromOracle(uint256 requestId, int256 temperature1) internal virtual;
}
